import React from 'react'
import { Col, Row, Image, Card } from 'react-bootstrap'
import { useAuth } from '../../../../AuthContext'
import testPic from '../../../../images/prof-icon1 1.png'
import Post from './Post'

useEffect(() => {
    setLoading(true)
    setError()
    const userList = []
    results.get('posts/.json')
        .then(function (response) {
            for (let i = 0; i < 2; i++) {
                const type = i === 0 ? 'establishment' : 'serviceagency'
                for (let key in response[i].data) {
                    userList.unshift(
                        {
                            ...response[i].data[key],
                            id: key,
                            userType: type
                        }
                    )
                }
            }
        })
        .catch(function (error) {
            setLoading(false)
            setError('Database Error')
        })
        .then(function () {
            setLoading(false)
            setUsers(userList)
        });

}, []);

const Profile = () => {
    const { userDetails } = useAuth()
    return (
        <div>
            <Card className='m-3 p-3'>
                <h3>My Profile</h3>
                <Row xs={1} sm={1} md={2}>
                    <Col className='mb-3'>
                        <div className='d-flex mt-2'>
                            <div className='profile-pic'>
                                <Image fluid src={testPic} />
                            </div>
                            <div className='ms-3'>
                                {userDetails.companyName} <br />
                                {userDetails.userName} <br />
                                {userDetails.userTitle}
                            </div>
                        </div>
                    </Col>

                    <Col fluid>
                        <h3>News Feed</h3>

                        {users.map((users) => (
                            <Post key={users.id}>


                            </Post>
                        ))}

                    </Col>
                </Row>
            </Card>
        </div>
    )
}

export default Profile
