import React, { useState } from 'react'
import { Button, Form, FloatingLabel } from 'react-bootstrap'
import results from '../../../../results'
import { useAuth } from '../../../../AuthContext'

const Edit = (props) => {

    const { currentUser } = useAuth()
    const service = props.editservice
    const [availability, setAvailability] = useState(service.availability)
    const [loading, setLoading] = useState()

    function handleSave(e) {
        e.preventDefault()
        const data = { availability: availability }
        results.put('/services/' + service.id + '/' + currentUser.uid + '.json', data)
    }

    function handleDelete(e) {
        e.preventDefault()
        results.delete('/services/' + service.id + '/' + currentUser.uid + '.json')
    }

    return (
        <>
            <Form className='p-3'>
                <Form.Group>
                    <FloatingLabel className='mb-3' label='Service Type' >
                        <Form.Control
                            placeholder='Service Type'
                            required
                            type='text'
                            value={service.name}
                            disabled={true}
                        />
                    </FloatingLabel>
                </Form.Group>
                <Form.Group>
                    <FloatingLabel className='mb-3' label='Availability' >
                        <Form.Control
                            placeholder='Availability'
                            required
                            value={availability}
                            type='text'
                            onChange={(e) => setAvailability(e.target.value)}
                        />
                    </FloatingLabel>
                </Form.Group>

                <div className='d-flex justify-content-center' style={{ gap: '5px' }}>
                    <Button
                        variant='danger'
                        type='submit'
                        style={{ minWidth: '150px' }}
                        onClick={handleDelete}
                        disabled={loading}
                    >
                        Delete
                    </Button>

                    <Button
                        variant='success'
                        type='submit'
                        style={{ minWidth: '150px' }}
                        onClick={handleSave}
                        disabled={loading}
                    >
                        Save
                    </Button>
                </div>
            </Form>
        </>
    )
}

export default Edit
