import React, { useState } from 'react'
import { Alert, Button, Card, Col, Container, Form, Image, Row, FloatingLabel } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import Navbar from '../../navbar/Navbar'
import SearchBar from '../../SearchBar';

import results from '../../../results'
import { useAuth } from '../../../AuthContext'
import Art from '../../../images/art/RegisterPageArt.svg'
import EstablishmentForm from './EstablishmentForm';

const Register = () => {
    const [contactPerson, setContactPerson] = useState('')
    const [companyName, setCompanyName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [checked, setChecked] = useState(false)

    const [error, setError] = useState('')
    const [success, setSuccess] = useState(false)
    const [loading, setLoading] = useState(false)

    const { signup, isAgency, currentUser } = useAuth()
    const userType = isAgency ? 'serviceagency' : 'establishment'
    var errorCheck = false;
    
    console.log(success)

    const handleCheck = () => {
        checked ? setChecked(false) : setChecked(true)
    }

    async function handleSubmit(e) {
        e.preventDefault();
        setSuccess(false);
        setLoading(true);
        if (!checked) {
            setError('Must accept Terms of Service and Privacy Policy')
            setLoading(false)
        }
        else if (password !== confirmPassword) {
            setError('Passwords do not match')
            setLoading(false)
        }
        else {
            try {
                setError()
                await signup(email, password)
            } catch {
                setError('Failed to create account');
                setLoading(false)
            }
            if(!error) {
                setLoading(false)
                setSuccess(true)
                console.log(error)
            }
        }
    }

    return (
        <div>
            <Navbar />
            <SearchBar />
            <Container className='mt-3'>
                <Row>
                    <Col>
                        <h3>Create Account</h3>
                        <Card>
                            {error && <Alert className='mx-3 mt-3 mb-0' variant='danger' style={{ fontSize: '14px' }}>{error} </Alert>}
                            {success && !error && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Account Created Successfully! Set up user information </Alert>}
                            <Card.Body>
                                
                                {!success && <Form>
                                    <Form.Group>
                                        <FloatingLabel
                                            className="mb-3"
                                            label="Email Address"
                                        >
                                            <Form.Control
                                                required
                                                type="email"
                                                placeholder="Email Adress"
                                                onChange={(e) => setEmail(e.target.value)}
                                            />
                                        </FloatingLabel>
                                    </Form.Group>

                                    <Form.Group>
                                        <FloatingLabel
                                            className="mb-3"
                                            label="Password"
                                        >
                                            <Form.Control
                                                required
                                                type="password"
                                                placeholder="Password"
                                                onChange={(e) => setPassword(e.target.value)}
                                            />
                                        </FloatingLabel>
                                    </Form.Group>
                                    <Form.Group>
                                        <FloatingLabel
                                            className="mb-3"
                                            label="Confirm Password"
                                        >
                                            <Form.Control
                                                required
                                                type="password"
                                                placeholder="Confirm Password"
                                                onChange={(e) => setConfirmPassword(e.target.value)}
                                            />
                                        </FloatingLabel>
                                    </Form.Group>
                                    <Form.Group className="d-flex">
                                        <Form.Check
                                            type="checkbox"
                                            onClick={handleCheck}
                                        />
                                        <Form.Label>
                                            <p className='ms-2'>By signing up you accept the
                                                <Link className='link-format red-font' to='/tos'> Terms of Service </Link>
                                                and
                                                <Link className='link-format red-font' to='/privacy'> Privacy Policy </Link>
                                            </p>
                                        </Form.Label>
                                    </Form.Group>
                                    <div className="d-grid gap-2">
                                        <Button
                                            variant="danger"
                                            type="submit"
                                            disabled={loading}
                                            onClick={handleSubmit}
                                        >
                                            Register
                                        </Button>
                                    </div>
                                </Form>}

                                {currentUser && success && !isAgency && <EstablishmentForm/>}

                            </Card.Body>
                        </Card>
                    </Col>

                    <Col className='d-none d-md-block d-lg-block d-xl-block' >
                        <Image src={Art} fluid />
                    </Col>
                </Row>
            </Container>
        </div>
    )
}

export default Register
