import React, { useState } from 'react'
import { Alert, Button, Form, FloatingLabel } from 'react-bootstrap'
import results from '../../../../results'
import { useAuth } from '../../../../AuthContext'
import Navbar from '../../../navbar/Navbar'
import SearchBar from '../../../SearchBar'

const Edit = (props) => {

    const id = props.key
    const { currentUser } = useAuth()
    const service = props.editservice
    const [availability, setAvailability] = useState()
    const [loading, setLoading] = useState(false)
    const [editSuccess, setEditSuccess] = useState(false)
    const [deleteSuccess, setDeleteSuccess] = useState(false)

    function handleSave(e) {
        e.preventDefault()
        setLoading(true)
        const data = { availability: availability }
        results.put('/services/' + service.id + '/' + currentUser.uid + '.json', data)
            .then(function () {
                setLoading(false)
                setEditSuccess(true)
            })
    }

    function handleDelete(e) {
        e.preventDefault()
        results.delete('/services/' + service.id + '/' + currentUser.uid + '.json')
            .then(function () {
                setLoading(true)
                setDeleteSuccess(true)
            })
    }

    return (
        <>
            <Navbar />
            <SearchBar />
            {id}
            <div className='p-3 px-5 mb-5' style={{ height: '100vh', width: '100vw' }}>
                {editSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Edit Success </Alert>}
                {deleteSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Delete Success </Alert>}
                <Form className='p-3'>
                    <Form.Group>
                        <FloatingLabel className='mb-3' label='Service Type' >
                            <Form.Control
                                placeholder='Service Type'
                                required
                                type='text'
                                // value={service.name}
                                disabled={true}
                            />
                        </FloatingLabel>
                    </Form.Group>
                    <Form.Group>
                        <FloatingLabel className='mb-3' label='Availability' >
                            <Form.Control
                                placeholder='Availability'
                                required
                                // value={availability}
                                type='text'
                            // onChange={(e) => setAvailability(e.target.value)}
                            />
                        </FloatingLabel>
                    </Form.Group>

                    <div className='d-flex justify-content-center' style={{ gap: '5px' }}>
                        <Button
                            variant='danger'
                            type='submit'
                            style={{ minWidth: '150px' }}
                            onClick={handleDelete}
                            disabled={loading}
                        >
                            Delete
                        </Button>

                        <Button
                            variant='success'
                            type='submit'
                            style={{ minWidth: '150px' }}
                            onClick={handleSave}
                            disabled={loading}
                        >
                            Save
                        </Button>
                    </div>
                </Form>
            </div>
        </>
    )
}

export default Edit
