import React, { useState } from 'react'
import { Card, Form, FloatingLabel, Button, Image } from 'react-bootstrap'
import { useAuth } from '../../../../AuthContext'
import { storage } from '../../../../firebase'
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage"
import results from '../../../../results'

const CreatePost = () => {

    const { currentUser } = useAuth()
    const [postTitle, setPostTitle] = useState()
    const [postDetails, setPostDetails] = useState()
    const [postActive, setPostActive] = useState(false)
    const [file, setFile] = useState()
    const [url, setUrl] = useState()
    const [loading, setLoading] = useState()
    const [success, setSuccess] = useState()
    const [error, setError] = useState()
    const date = new Date()

    function handleBoxClick() {
        !postActive ? setPostActive(true) : setPostActive(false)
    }

    function handleUpload(file) {
        setLoading(true)
        setFile(file)
        const metadata = {
            contentType: file.type
        };
        const filePath = 'images/' + '' + date.getFullYear() + '' + (date.getMonth() + 1) + '' + date.getDay() + '' + date.getHours() + date.getMinutes() + file.name.substring(file.name.length - 10)
        const storageRef = ref(storage, filePath)
        const uploadTask = uploadBytesResumable(storageRef, file, metadata)
        uploadTask.on('state_changed',
            (snapshot) => {
                const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log('Upload is ' + progress + '% done')
                switch (snapshot.state) {
                    case 'paused':
                        console.log('Upload is paused')
                        break;
                    case 'running':
                        console.log('Upload is running')
                        break
                }
            },
            (error) => {
                // A full list of error codes is available at
                // https://firebase.google.com/docs/storage/web/handle-errors
                switch (error.code) {
                    case 'storage/unauthorized':
                        // User doesn't have permission to access the object
                        break;
                    case 'storage/canceled':
                        // User canceled the upload
                        break;

                    // ...

                    case 'storage/unknown':
                        // Unknown error occurred, inspect error.serverResponse
                        break;

                    default:
                }
            },
            () => {
                // Upload completed successfully, now we can get the download URL
                getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
                    setUrl(downloadURL)
                    console.log(downloadURL)
                    setLoading(false)
                });
            }
        );

    }

    function handleSubmitClick() {
        console.log(postTitle, postDetails, postActive, url, date)
        results.post('/posts.json', {
            title: postTitle,
            details: postDetails,
            active: postActive,
            imageRef: url,
            date: date
        }).then(function () {
            setSuccess(true)
        }).catch(function (error) {
            setError(error)
        })
    }

    return (
        <div>
            <div className='d-flex justify-content-center'>
                <Card className='m-3' style={{ width: '800px' }}>
                    <Card.Header className='bg-danger text-white'>
                        Create Post
                    </Card.Header>
                    <Card.Body>
                        <Form>
                            <Form.Group>
                                <FloatingLabel className='mb-3' label='Title' >
                                    <Form.Control
                                        placeholder='Title'
                                        required type='text'
                                        onChange={(e) => setPostTitle(e.target.value)}
                                    />
                                </FloatingLabel>
                            </Form.Group>
                            <Form.Group className='mb-2' controlId='exampleForm.ControlTextarea1'>
                                <Form.Control
                                    as='textarea'
                                    rows={3}
                                    placeholder='Description'
                                    onChange={(e) => setPostDetails(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group>
                                <Form.Check
                                    type='checkbox'
                                    label='Active'
                                    onClick={handleBoxClick}
                                />
                            </Form.Group>

                            <Form.Group controlId="formFile" className="mb-3">
                                <Form.Label>Attachment</Form.Label>
                                <Form.Control type="file" onChange={(e) => handleUpload(e.target.files[0])} />
                            </Form.Group>

                            <div className='d-flex justify-content-end'>
                                <Button variant='secondary' onClick={handleSubmitClick} disabled={loading}>
                                    Post {'>'}
                                </Button>
                            </div>

                        </Form>
                    </Card.Body>
                    <Image className='post-pic' src={url} />
                </Card>
            </div>
        </div>
    )
}

export default CreatePost
