import React, { useEffect, useState } from 'react'
import { Button, Card, Col, Container, Form, Row } from 'react-bootstrap'
import results from '../../../../results'

const View = () => {

    const [hasQuery, setHasQuery] = useState(false)
    const [services, setServices] = useState([])
    const [serviceQuery, setServiceQuery] = useState()
    const [queryID, setQueryID] = useState('default')
    const [agencies, setAgencies] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(() => {
        const serviceList = []
        results.get('/services.json')
            .then(function (response) {
                for (let key in response.data) {
                    serviceList.unshift(
                        {
                            id: key,
                            name: response.data[key].serviceName
                        }
                    )
                }
            }).then(function () {
                setServices(serviceList)
            })
    }, [])

    async function getAgencies() {
        const agencyList = []
        if (queryID !== 'default') {
            results.get('/services/' + queryID + '/.json')
                .then(function (response) {
                    setServiceQuery(response.data.serviceName)
                    for (let key in response.data) {
                        if (key !== 'serviceName') {
                            results.get('/users/serviceagency/' + key + '.json')
                                .then(function (response2) {
                                    agencyList.unshift(
                                        {
                                            name: response2.data.companyName,
                                            availability: response.data[key].availability
                                        }
                                    )
                                }).finally(function () {

                                    setAgencies(agencyList)
                                    setHasQuery(true)
                                    setLoading(false)
                                })
                        }
                    }
                })
        }
    }

    function handleApply(e) {
        e.preventDefault()
        setHasQuery(false)
        setLoading(true)
        getAgencies()
    }

    return (
        <div className='mt-3 p-3'>
            <Row xs={1} sm={1} md={2}>
                <Col className='mb-3'>
                    <Card>
                        <Container>
                            <h1 className='mt-3'>View Services</h1>
                            <Form className='p-3 mb-3'>
                                <Form.Select
                                    aria-label="Default select example"
                                    onChange={(e) => setQueryID(e.target.value)}
                                >
                                    <option value='default'>Select Service Type</option>
                                    {services.map((service) =>
                                        <option key={service.id} value={service.id}>{service.name}</option>
                                    )}
                                </Form.Select>

                                <div className='ps-3 mt-3 d-flex justify-content-between'>
                                    <Button
                                        variant='light'
                                        style={{ minWidth: '150px' }}
                                        onClick={(e) => setHasQuery(false)}
                                        disabled={!hasQuery}
                                    >
                                        Reset
                                    </Button>

                                    <Button
                                        variant='success'
                                        style={{ minWidth: '150px' }}
                                        onClick={handleApply}
                                    >
                                        Apply
                                    </Button>
                                </div>

                            </Form>
                        </Container>
                    </Card>
                </Col>

                {hasQuery && !loading && <Col>
                    <Card>
                        <Card className='bg-danger text-white'>
                            <Card.Header>
                                Services: {serviceQuery}
                            </Card.Header>
                            <Card.Body>
                                {agencies.map((agency) =>
                                    <div className='d-flex justify-content-between'
                                        key={agency.name}
                                    >
                                        <span>{agency.name}</span>
                                        <span>{agency.availability}</span>
                                    </div>
                                )}
                            </Card.Body>
                        </Card>
                    </Card>
                </Col>}
            </Row>
        </div>
    )
}

export default View
