import React, { useState } from 'react'
import { Card, Form, FloatingLabel, Button } from 'react-bootstrap'
import { useAuth } from '../../../../AuthContext'
import { app } from '../../../../firebase'
import { getStorage, ref, uploadBytes } from "firebase/storage"

const CreatePost = () => {

    const { currentUser } = useAuth()
    const [postTitle, setPostTitle] = useState()
    const [postDetails, setPostDetails] = useState()
    const [postActive, setPostActive] = useState(false)
    const [postImg, setPostImg] = useState()

    function handleBoxClick() {
        !postActive ? setPostActive(true) : setPostActive(false)
    }

    function handleSubmitClick() {
        console.log(currentUser.uid)
        console.log(postTitle)
        console.log(postDetails)
        console.log(postActive)
        console.log(postImg)


        if (postImg) {
            const storage = getStorage(app)
            const storageRef = ref(storage, 'posts/' + postImg.name)
            uploadBytes(storageRef, postImg.name).then((snapshot) => {
                console.log('Upload Succysuccy');
            })
        }



    }

    return (
        <div>
            <div className='d-flex justify-content-center'>
                <Card className='m-3' style={{ width: '800px' }}>
                    <Card.Header className='bg-danger text-white'>
                        Create Post
                    </Card.Header>
                    <Card.Body>
                        <Form>
                            <Form.Group>
                                <FloatingLabel className='mb-3' label='Title' >
                                    <Form.Control
                                        placeholder='Title'
                                        required type='text'
                                        onChange={(e) => setPostTitle(e.target.value)}
                                    />
                                </FloatingLabel>
                            </Form.Group>
                            <Form.Group className='mb-2' controlId='exampleForm.ControlTextarea1'>
                                <Form.Control
                                    as='textarea'
                                    rows={3}
                                    placeholder='Description'
                                    onChange={(e) => setPostDetails(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group>
                                <Form.Check
                                    type='checkbox'
                                    label='Active'
                                    onClick={handleBoxClick}
                                />
                            </Form.Group>

                            <Form.Group controlId="formFile" className="mb-3">
                                <Form.Label>Attachment</Form.Label>
                                <Form.Control type="file" onChange={(e) => setPostImg(e.target.files[0])} />
                            </Form.Group>

                            <div className='d-flex justify-content-end'>
                                <Button variant='secondary' onClick={handleSubmitClick}>
                                    Post {'>'}
                                </Button>
                            </div>

                        </Form>
                    </Card.Body>
                </Card>
            </div>
        </div>
    )
}

export default CreatePost
