import React, { useEffect, useState } from 'react'
import Navbar from '../../components/navbar/AdminNavbar'
import { Alert, Button, Card, Container, Col, Image, Row, ListGroup, Modal } from 'react-bootstrap'
import Art from '../../images/art/AdminManageUsersArt.svg'
import results from '../../results'
import editIcon from '../../images/icons/EditIcon.svg'



const ManageUsers = () => {

    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState();
    const [error, setError] = useState();
    const [modalShow, setModalShow] = useState(false);
    const [ID, setID] = useState();

    useEffect(() => {
        setLoading(true)
        setError()
        const userList = []
        Promise.all([results.get('users/establishment.json'), results.get('users/serviceagency.json')])
            .then(function (response) {
                for (let i = 0; i < 2; i++) {
                    const type = i === 0 ? 'establishment' : 'serviceagency'
                    for (let key in response[i].data) {
                        userList.unshift(
                            {
                                ...response[i].data[key],
                                id: key,
                                userType: type
                            }
                        )
                    }
                }
            })
            .catch(function (error) {
                setLoading(false)
                setError('Database Error')
            })
            .then(function () {
                setLoading(false)
                setUsers(userList)
            });

    }, []);

    function EditModal(props) {
        return (
            <Modal
                {...props}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Edit User
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* Contact Person
                        Company Name
                        Email Add
                        Password
                        Premium
                    */}
                    <Form>
                        <Form.Group className="mb-3" controlId="signInEmail">
                            <Form.Control
                                required
                                type="email"
                                placeholder="Email"
                                onChange={(e) => setEmail(e.target.value)} />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="signInPassword">
                            <Form.Control
                                required
                                type="password"
                                placeholder="Password"
                                onChange={(e) => setPassword(e.target.value)} />
                        </Form.Group>
                        <div className="d-grid gap-2">
                            <Button variant="success"
                                type="submit"
                                onClick={handleSubmit}>
                                Login
                            </Button>
                        </div>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
    }

    function handleClick(id) {
        setID(id)
        setModalShow(true)
    }

    return (
        <div>
            <Navbar />
            <h1 className='text-center my-5' >Manage Users</h1>
            <Container>
                <Row style={{ gap: '60px' }} >
                    <Col>
                        <Card>
                            {error && <Alert variant='danger'>{error}</Alert>}
                            <Card.Body >
                                {loading && <div>
                                    <h3>Loading...</h3>
                                </div>}

                                {!loading && users.length === 0 && <div>
                                    <h3>There are no users</h3>
                                </div>}

                                {!loading && users.length !== 0 && <div>
                                    <h3> Users </h3>
                                    <ListGroup className='users-card'>
                                        {users.map((users) => (
                                            <ListGroup.Item key={users.id}>
                                                <Row>
                                                    <Col xs='5'> {users.companyName} </Col>
                                                    <Col xs='5'> {users.contactPerson} </Col>
                                                    <Col xs='1'>
                                                        <Button className='edit-button p-0'
                                                            variant='light'
                                                            onClick={() => handleClick(users.id)}
                                                        >
                                                            <Image style={{ marginTop: '-10px', height: '14px', width: 'auto' }}
                                                                src={editIcon} alt='editicon' />
                                                        </Button>
                                                    </Col>
                                                </Row>
                                                <Row>
                                                    <Col> {users.email} </Col>
                                                    <Col> {users.userType} </Col>
                                                </Row>
                                            </ListGroup.Item>
                                        ))}
                                    </ListGroup>
                                </div>}
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col className='d-none d-md-block d-lg-block d-xl-block'>
                        <Image src={Art} fluid />
                    </Col>
                </Row>
            </Container>

            <EditModal
                show={modalShow}
                onHide={() => setModalShow(false)}
            />

        </div >
    )
}

export default ManageUsers;
