import React, { useEffect, useState } from 'react'
import Navbar from '../../../navbar/Navbar'
import { Card, Row, Form, Col, ListGroup, Alert, Modal, Button, FloatingLabel } from 'react-bootstrap'

import SearchBar from '../../../SearchBar'
import Edit from './Edit'
import Add from './Add'

import results from '../../../../results'
import { useAuth } from '../../../../AuthContext'

const Services = () => {

    const { currentUser } = useAuth()
    const [error, setError] = useState()
    const [services, setServices] = useState([])
    const [editService, setEditService] = useState()
    const [modalShow, setModalShow] = useState(false);
    const [loading, setLoading] = useState(false)
    const [editSuccess, setEditSuccess] = useState(false)
    const [deleteSuccess, setDeleteSuccess] = useState(false)

    useEffect(() => {
        const serviceList = []
        results.get('/services.json')
            .then(function (response) {
                for (let key in response.data) {
                    if (response.data[key][currentUser.uid]) {
                        serviceList.unshift(
                            {
                                name: response.data[key].serviceName,
                                availability: response.data[key][currentUser.uid].availability,
                                id: key,
                            }
                        )
                    }
                }
            }).catch(function () {
                setError('Something went wrong. (Database Error)')
            }).then(function () {
                setServices(serviceList)
            })
    }, [])

    function handleSave(e) {
        e.preventDefault()
        setLoading(true)
        const data = { availability: availability }
        results.put('/services/' + service.id + '/' + currentUser.uid + '.json', data)
            .then(function () {
                setLoading(false)
                setEditSuccess(true)
            })
    }

    function handleDelete(e) {
        e.preventDefault()
        results.delete('/services/' + service.id + '/' + currentUser.uid + '.json')
            .then(function() {
                setLoading(true)
                setDeleteSuccess(true)
            })
    }


    function handleClick(service) {
        setModalShow(true)
        setEditService(service)
    }

    function EditModal(props) {
        return (
            <Modal
                {...props}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Edit Service
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {editSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Edit Success </Alert>}
                    {deleteSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Delete Success </Alert>}
                    <Form className='p-3'>
                        <Form.Group>
                            <FloatingLabel className='mb-3' label='Service Type' >
                                <Form.Control
                                    placeholder='Service Type'
                                    required
                                    type='text'
                                    value={editService.name}
                                    disabled={true}
                                />
                            </FloatingLabel>
                        </Form.Group>
                        <Form.Group>
                            <FloatingLabel className='mb-3' label='Availability' >
                                <Form.Control
                                    placeholder='Availability'
                                    required
                                    value={availability}
                                    type='text'
                                    onChange={(e) => setAvailability(e.target.value)}
                                />
                            </FloatingLabel>
                        </Form.Group>

                        <div className='d-flex justify-content-center' style={{ gap: '5px' }}>
                            <Button
                                variant='danger'
                                type='submit'
                                style={{ minWidth: '150px' }}
                                onClick={handleDelete}
                                disabled={loading}
                            >
                                Delete
                            </Button>

                            <Button
                                variant='success'
                                type='submit'
                                style={{ minWidth: '150px' }}
                                onClick={handleSave}
                                disabled={loading}
                            >
                                Save
                            </Button>
                        </div>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        )
    }

    return (
        <div>
            <Navbar />
            <SearchBar />

            <div className='p-3 px-5 mb-5' style={{ height: '100vh', width: '100vw' }}>
                <h1> Manage Services </h1>
                <Card className='mt-3 p-3' style={{ height: '100vh' }}>
                    <Row xs={1} sm={1} md={2}>
                        <Col>
                            <h3>My Services</h3>
                            {error && <Alert className='mx-3 mt-3 mb-0' variant='danger' style={{ fontSize: '14px' }}> {error} </Alert>}
                            <ListGroup className='users-card overflow mt-3'>
                                {services.map((service) => (
                                    <ListGroup.Item key={service.id} action onClick={(e) => handleClick(service)}>
                                        <div className='d-flex justify-content-between'>
                                            <span>{service.name}</span>
                                            <span>{service.availability}</span>
                                        </div>
                                    </ListGroup.Item>
                                ))}
                                <ListGroup.Item action onClick={() => console.log('Add')}> + Add Item </ListGroup.Item>
                            </ListGroup>
                        </Col>

                        <Col>
                            <Add />

                        </Col>
                    </Row>
                </Card>

                <EditModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                />

            </div>
        </div>
    )
}

export default Services
