import React, { useState, useEffect } from 'react'
import { Alert, Button, Card, Col, Image, Modal, Row } from 'react-bootstrap'
import { useAuth } from '../../../../AuthContext'
import testPic from '../../../../images/prof-icon1 1.png'
import Post from './Post'
import results from '../../../../results'
import EditForm from './EditProfileForm'

const Profile = () => {

    const { currentUser, userDetails } = useAuth()
    const [loading, setLoading] = useState()
    const [error, setError] = useState()
    const [posts, setPosts] = useState([])
    const [services, setServices] = useState([])
    const [modalShow, setModalShow] = useState(false)

    useEffect(() => {
        setLoading(true)
        setError()
        const postList = []
        results.get('/posts/' + currentUser.uid + '/.json')
            .then(function (response) {
                for (let key in response.data) {
                    postList.unshift(
                        {
                            ...response.data[key],
                            id: key
                        }
                    )
                }
            })
            .catch(function (error) {
                setLoading(false)
                setError('Database Error')
            })
            .then(function () {
                setLoading(false)
                setPosts(postList)
            })
    }, [])

    useEffect(() => {
        const serviceList = []
        results.get('/services.json')
            .then(function (response) {
                for (let key in response.data) {
                    if (response.data[key][currentUser.uid]) {
                        serviceList.unshift(
                            {
                                id: key,
                                name: response.data[key].serviceName,
                                availability: response.data[key][currentUser.uid].availability
                            }
                        )
                    }
                }
            }).catch(function (error) {
                setLoading(false)
                setError('Database Error')
            })
            .then(function () {
                setLoading(false)
                setServices(serviceList)
            })
    }, [])

    function handleEdit(e) {
        e.preventDefault()
        setModalShow(true)
        console.log('edit')
    }

    function EditModal(props) {
        return (
            <Modal
                {...props}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Edit User
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <EditForm details={userDetails} uid={currentUser.uid} />
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        )
    }

    return (
        <div>
            <Card className='m-3 p-3'>
                <Row xs={1} sm={1} md={2}>
                    <Col className='mb-3'>
                        <div className='d-flex justify-content-between'>
                            <h2>My Profile</h2>
                            <Button
                                variant='success'
                                onClick={handleEdit}
                            >
                                Edit Profile
                            </Button>
                        </div>
                        <div className='d-flex mt-3'>
                            <div className='profile-pic'>
                                <Image fluid src={testPic} />
                            </div>
                            <div className='ms-3'>
                                <h5>{userDetails.companyName}</h5>
                                <div className='text-muted'>{userDetails.userName}</div>
                                {userDetails.userTitle}
                            </div>
                        </div>

                        <div className='mt-3 p-3' style={{ maxWidth: '450px' }}>
                            <div className='d-flex justify-content-between fw-bold'>
                                <p>Services</p>
                                <p>Availability</p>
                            </div>
                            {services.map((service) =>
                                <div key={service.id} className='d-flex justify-content-between'>
                                    <a>{service.name}</a>
                                    <a>{service.availability}</a>
                                </div>
                            )}
                        </div>
                    </Col>

                    <Col>
                        {error && <Alert className='mx-3 mt-3 mb-0' variant='danger' style={{ fontSize: '14px' }}>{error} </Alert>}
                        <h3 className='mb-3'>News Feed</h3>
                        {posts.length === 0 && <div>
                            Your feed is empty
                        </div>}
                        {!loading && posts.length !== 0 && <div>
                            <div>
                                {posts.map((post) =>
                                    <Post key={post.id} post={post} />
                                )}
                            </div>
                        </div>}
                    </Col>
                </Row>
            </Card>

            <EditModal
                show={modalShow}
                onHide={() => setModalShow(false)}
            />
        </div>
    )
}
export default Profile
