import React, { useState, useEffect } from 'react'
import { Alert, Button, ButtonGroup, Card, Col, Image, Row } from 'react-bootstrap'
import { useAuth } from '../../../../../AuthContext'
import Post from './Post'
import results from '../../../../../results'
import EditProfile from './EditProfile'

const Profile = () => {

    const { currentUser, userDetails } = useAuth()
    const [loading, setLoading] = useState()
    const [error, setError] = useState()
    const [posts, setPosts] = useState([])
    const [services, setServices] = useState([])
    console.log(userDetails)

    useEffect(() => {
        setLoading(true)
        setError()
        const postList = []
        results.get('/posts/' + currentUser.uid + '/.json')
            .then(function (response) {
                for (let key in response.data) {
                    postList.unshift(
                        {
                            ...response.data[key],
                            id: key
                        }
                    )
                }
            })
            .catch(function (error) {
                setLoading(false)
                setError('Database Error')
            })
            .then(function () {
                setLoading(false)
                setPosts(postList)
            })
    }, [])

    useEffect(() => {
        const serviceList = []
        results.get('/services.json')
            .then(function (response) {
                for (let key in response.data) {
                    if (response.data[key][currentUser.uid]) {
                        serviceList.unshift(
                            {
                                id: key,
                                name: response.data[key].serviceName,
                                availability: response.data[key][currentUser.uid].availability
                            }
                        )
                    }
                }
            }).catch(function (error) {
                setLoading(false)
                setError('Database Error')
            })
            .then(function () {
                setLoading(false)
                setServices(serviceList)
            })
    }, [])

    function handleEdit(e) {
        e.preventDefault()
    }

    return (
        <div>
            <Card className='m-3 p-3'>
                <Row xs={1} sm={1} md={2}>
                    <Col className='mb-3'>
                        <div className='d-flex justify-content-between'>
                            <h2>My Profile</h2>
                            <ButtonGroup>
                                <Button
                                    variant='danger'
                                    onClick={handleEdit}
                                >
                                    View Profile
                                </Button>
                                <Button
                                    variant='secondary'
                                    onClick={handleEdit}
                                >
                                    Edit Profile
                                </Button>

                            </ButtonGroup>
                        </div>
                        <EditProfile />
                    </Col>

                    <Col>
                        {error && <Alert className='mx-3 mt-3 mb-0' variant='danger' style={{ fontSize: '14px' }}>{error} </Alert>}
                        <h3 className='mb-3'>News Feed</h3>
                        {posts.length === 0 && <div>
                            Your feed is empty
                        </div>}
                        {!loading && posts.length !== 0 && <div>
                            <div>
                                {posts.map((post) =>
                                    <Post key={post.id} post={post} />
                                )}
                            </div>
                        </div>}
                    </Col>
                </Row>
            </Card>
        </div>
    )
}
export default Profile
