import React, { useState } from 'react'
import { Card, Form, FloatingLabel, Button, Image } from 'react-bootstrap'
import { useAuth } from '../../../../AuthContext'
import { storage } from '../../../../firebase'
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage"
import results from '../../../../results'

const CreatePost = () => {

    const { currentUser } = useAuth()
    const [postTitle, setPostTitle] = useState()
    const [postDetails, setPostDetails] = useState()
    const [postActive, setPostActive] = useState(false)
    const [file, setFile] = useState()
    const [url, setUrl] = useState()
    const [error, setError] = useState()

    const current = new Date();
    const date = current.getDate() + '/' + (current.getMonth() + 1) + '/' + current.getFullYear()

    function handleBoxClick() {
        !postActive ? setPostActive(true) : setPostActive(false)
    }

    function handleSubmitClick(e) {
        e.preventDefault()
        console.log(currentUser, postTitle, postDetails)
        console.log(file)
        if (file) {
            const metadata = {
                contentType: 'image/jpeg'
            };

            const storageRef = ref(storage, 'images/' + file.name)
            const uploadTask = uploadBytesResumable(storageRef, file, metadata)
            uploadTask.on('state_changed',
                (snapshot) => {
                    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                    console.log('Upload is ' + progress + '% done')
                },
                (error) => {
                    switch (error.code) {
                        case 'storage/canceled':
                            // User canceled the upload
                            break
                        case 'storage/unknown':
                            // Unknown error occurred, inspect error.serverResponse
                            break

                        default:
                    }
                },
                () => {
                    // Upload completed successfully, now we can get the download URL
                    getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
                        setUrl(downloadURL)
                    })
                }
            )
        }

        results.post('/posts.json', {
            title: postTitle,
            details: postDetails,
            active: postActive,
            imageRef: url,
            date: current
        }).then(function () {

        }).catch(function (error) {
            setError(error)
        })
    }

    return (
        <div>
            <div className='d-flex justify-content-center'>
                <Card className='m-3' style={{ width: '800px' }}>
                    <Card.Header className='bg-danger text-white'>
                        Create Post
                    </Card.Header>
                    <Card.Body>
                        <Form>
                            <Form.Group>
                                <FloatingLabel className='mb-3' label='Title' >
                                    <Form.Control
                                        placeholder='Title'
                                        required type='text'
                                        onChange={(e) => setPostTitle(e.target.value)}
                                    />
                                </FloatingLabel>
                            </Form.Group>
                            <Form.Group className='mb-2' controlId='exampleForm.ControlTextarea1'>
                                <Form.Control
                                    as='textarea'
                                    rows={3}
                                    placeholder='Description'
                                    onChange={(e) => setPostDetails(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group>
                                <Form.Check
                                    type='checkbox'
                                    label='Active'
                                    onClick={handleBoxClick}
                                />
                            </Form.Group>

                            <Form.Group controlId="formFile" className="mb-3">
                                <Form.Label>Attachment</Form.Label>
                                <Form.Control type="file" onChange={(e) => setFile(e.target.files[0])} />
                            </Form.Group>

                            <div className='d-flex justify-content-end'>
                                <Button variant='secondary' onClick={handleSubmitClick} type='submit'>
                                    Post {'>'}
                                </Button>
                            </div>

                        </Form>
                    </Card.Body>
                    <Image src={url} />
                </Card>
            </div>
        </div>
    )
}

export default CreatePost
