import React, { useEffect, useState } from 'react'
import { Alert, Button, Card, Form, FloatingLabel } from 'react-bootstrap'
import { useParams } from 'react-router'
import results from '../../../../results'
import { useAuth } from '../../../../AuthContext'
import Navbar from '../../../navbar/Navbar'
import SearchBar from '../../../SearchBar'

const Edit = () => {

    const { id } = useParams()
    const { currentUser } = useAuth()

    const [serviceName, setServiceName] = useState()
    const [availability, setAvailability] = useState()

    const [loading, setLoading] = useState(false)
    const [editSuccess, setEditSuccess] = useState(false)
    const [deleteSuccess, setDeleteSuccess] = useState(false)

    const [test, setTest] = useState()

    useEffect(() => {
        results.get('/services/' + id + '/.json')
            .then(function (response) {
                setServiceName(response.data.serviceName)
                setAvailability(response.data[currentUser.uid].availability)
            })
    }, [])

    function handleSave(e) {
        e.preventDefault()
        setLoading(true)
        const data = { availability: availability }
        results.put('/services/' + id + '/' + currentUser.uid + '.json', data)
            .then(function () {
                setLoading(false)
                setEditSuccess(true)
            })
    }

    function handleDelete(e) {
        e.preventDefault()
        results.delete('/services/' + id + '/' + currentUser.uid + '.json')
            .then(function () {
                setLoading(true)
                setDeleteSuccess(true)
            })
    }

    return (
        <>
            <Navbar />
            <SearchBar />
            <div className='p-3 px-5 mb-5 d-flex' style={{ height: '100vh', width: '100vw' }}>
                <h1>Edit Service</h1>
                <Card style={{maxWidth:'300px'}}>
                    {editSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Edit Success </Alert>}
                    {deleteSuccess && <Alert className='mx-3 mt-3 mb-0' variant='success' style={{ fontSize: '14px' }}> Delete Success </Alert>}
                    <Form className='p-3'>
                        <Form.Group>
                            <FloatingLabel className='mb-3' label='Service Type' >
                                <Form.Control
                                    placeholder='Service Type'
                                    required
                                    type='text'
                                    value={serviceName}
                                    disabled={true}
                                />
                            </FloatingLabel>
                        </Form.Group>
                        <Form.Group>
                            <FloatingLabel className='mb-3' label='Availability' >
                                <Form.Control
                                    placeholder='Availability'
                                    required
                                    value={availability}
                                    type='text'
                                    onChange={(e) => setAvailability(e.target.value)}
                                />
                            </FloatingLabel>
                        </Form.Group>

                        <div className='d-flex justify-content-center' style={{ gap: '5px' }}>
                            <Button
                                variant='danger'
                                type='submit'
                                style={{ minWidth: '150px' }}
                                onClick={handleDelete}
                                disabled={loading}
                            >
                                Delete
                            </Button>

                            <Button
                                variant='success'
                                type='submit'
                                style={{ minWidth: '150px' }}
                                onClick={handleSave}
                                disabled={loading}
                            >
                                Save
                            </Button>
                        </div>
                    </Form>
                </Card>
            </div>
        </>
    )
}

export default Edit
