import React, { useState, useEffect } from 'react'
import { Form, Button, FloatingLabel } from 'react-bootstrap'
import results from '../results'

const EditForm = (props) => {
    const uid = props.uid
    const type = props.type

    const [contactPerson, setContactPerson] = useState()
    const [companyName, setCompanyName] = useState()
    const [email, setEmail] = useState()
    const [premiumExpiry, setPremiumExpiry] = useState()

    var d = new Date()
    var date = d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate()

    console.log(date)

    useEffect(() => {
        results.get('/users/' + type + '/' + uid + '.json')
            .then(function (response) {
                console.log(response.data)
                setCompanyName(response.data.companyName)
            }).catch(function (error) {
                //error here
                console.log(error)
            })
    }, [])



    const isAgency = props.type === 'serviceagency' ? true : false
    return (
        <div>
            {isAgency && <Form>
                <Form.Group controlId='editContact'>
                    <FloatingLabel
                        controlId="floatingInput"
                        label="Contact Person"
                        className="mb-3"
                    >
                        <Form.Control
                            required
                            type="text"
                            placeholder="Contact Person"
                            value={contactPerson}
                            onChange={(e) => setContactPerson(e.target.value)}
                        />
                    </FloatingLabel>
                </Form.Group>

                <Form.Group controlId="editCompany">
                    <FloatingLabel
                        controlId="floatingInput"
                        label="Company Name"
                        className="mb-3"
                    >
                        <Form.Control
                            required
                            type="text"
                            placeholder="Company Name"
                            value={companyName}
                            onChange={(e) => setCompanyName(e.target.value)}
                        />
                    </FloatingLabel>
                </Form.Group>

                <Form.Group controlId="editEmail">
                    <FloatingLabel
                        controlId="floatingInput"
                        label="Email Address"
                        className="mb-3"
                    >
                        <Form.Control
                            required
                            type="email"
                            placeholder="Email Address"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </FloatingLabel>
                </Form.Group>

                <Form.Group>
                    <FloatingLabel
                        controlId="floatingInput"
                        label="Premium Expiry"
                        className="mb-3"
                    >
                        <Form.Control
                            type='date'
                            value={premiumExpiry}
                            onChange={(e) => setPremiumExpiry(e.target.value)}
                        // onChange={(e) => setSomething(e.target.value)}
                        />
                    </FloatingLabel>
                </Form.Group>

                <div className="d-grid gap-2">
                    <Button variant="success"
                        type="submit"
                    // onClick={handleSubmit}
                    >
                        Submit
                    </Button>
                    <hr />
                    {contactPerson + companyName + email + premiumExpiry}
                </div>

            </Form>}
        </div>
    )
}

export default EditForm
